import React, {useState} from 'react';
import './index.scss';

export const TabDescriptor = (props) => {
  const {tabs = [], click} = props;
  const [activeTab, setActiveTab]  = useState(0);

  const setActive = (index, active) => {
    return index === active ? 'active' : ''; 
  };

  const handleOnClick = (index) => {
    setActiveTab(index);
  };

  return (
    <div className='tabs-container'>
      <ul className='tabs'>
        <li 
          onClick={() => {
          handleOnClick(-1);
            click(undefined);
          }}
          className={setActive(-1, activeTab)}>
          <span>Todas</span>
        </li>
        {tabs.map((value, index) => {
          return (
            <li 
              key={index}
              onClick={() => {
                handleOnClick(index);
                click(value.id);
              }}
              className={setActive(index, activeTab)}>
              <span>{value.name}</span>
            </li>
          );
        }) } 

      </ul>
      <div className='tabs-content'>
        <div className='tabs-panel active' data-index="0">
          <p>Loremp impsum Â¡Me encanta el sitio web! . Consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
        </div>
        <div className='tabs-panel' data-index="1">
        </div>
        <div className="tabs-panel" data-index="2">
        </div>
      </div>
    </div>
  );
};
