import React from "react";
import { Modal, FormControl, TextField, Button } from "@material-ui/core";

import { FieldTitle } from "../../atoms";
import Icons from "../../../resources/icons";
import { useSimpleForm } from "../../../hooks";
import "./index.scss";

const initialFormState = {
  offer: "",
  message: "",
};
export const ServiceBidModal = ({ open, setIsOpen }) => {
  const { values, onChange } = useSimpleForm(initialFormState);

  return (
    <Modal
      className="modal"
      open={open}
      disableEnforceFocus={true}
      disableAutoFocus={true}
    >
      <div id="service-bid-modal" className="box">
        <div className="service-modal-body">
          <div className="modal-title-container">
            <span className="modal-title">Oferta de precio</span>
            <svg
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
              onClick={() => setIsOpen(false)}
            >
              <path
                d="M24 2.41714L21.5829 0L12 9.58286L2.41714 0L0 2.41714L9.58286 12L0 21.5829L2.41714 24L12 14.4171L21.5829 24L24 21.5829L14.4171 12L24 2.41714Z"
                fill="#9FABAE"
                onClick={() => setIsOpen(false)}
              />
            </svg>
          </div>
          <hr></hr>
          <p className="modal-question">
            ¿Deseas hacer una oferta a John Doe en sus servicios de clases de
            matemáticas?
          </p>
          <p>Deja tu propuesta aquí y espera su respuesta pronto.</p>
          <FormControl className="field-container">
            <FieldTitle icon={Icons.price} label="Offer" />
            <TextField
              type="text"
              name="offer"
              className="field-input"
              variant="outlined"
              margin="normal"
              value={values.offer}
              onChange={onChange}
            />
          </FormControl>
          <FormControl className="field-container">
            <FieldTitle icon={Icons.messages} label="Message" />
            <TextField
              type="text"
              name="message"
              className="field-input"
              variant="outlined"
              margin="normal"
              value={values.message}
              onChange={onChange}
            />
          </FormControl>
        </div>
        <div className="modal-footer box">
          <Button variant="contained" color="primary">
            Enviar
          </Button>
        </div>
      </div>
    </Modal>
  );
};
