import React from "react";

import { SliderItemInvert } from "../../molecules";
import { useSlider } from "../../../hooks";
import "./index.css";

const news = [
  {
    title: "Este es el slide invertido",
    content:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
  },
  {
    title: "Este es el slide invertido 2",
    content:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
  },
  {
    title: "Este es el slide invertido 3",
    content:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
  },
];

export const SliderInvert = () => {
  const { previous, current, next, goBack, goNext } = useSlider(news);

  const showSlide = (slideIndex, callback) => {
    document.getElementById(`slide-${slideIndex}`).scrollIntoView();
    callback();
  };

  return (
    <div id="slider" className="slider-container border-radius secondary-20">
      <div className="slides-container">
        {news.map((newItem, index) => {
          return (
            <SliderItemInvert id={`slide-${index}`} key={index} {...newItem} />
          );
        })}
      </div>
      <div className="slider-controls-container">
        <div className="slider-controls-buttons">
          <svg
            width="30"
            height="18"
            viewBox="0 0 30 18"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            onClick={() => showSlide(previous, goBack)}
          >
            <path
              d="M8.1395 0.941454C8.378 1.19651 8.378 1.61045 8.1395 1.8654L2.0842 8.3465H29.3895C29.7266 8.3465 30 8.63913 30 8.99993C30 9.36073 29.7266 9.65336 29.3895 9.65336H2.0843L8.1397 16.1346C8.378 16.3896 8.3781 16.8035 8.1396 17.0586C7.9012 17.3138 7.5146 17.3138 7.2762 17.0585L0.178801 9.4619C0.0596008 9.33438 0 9.1671 0 8.99993C0 8.83277 0.0595989 8.66549 0.1789 8.53796L7.2763 0.941454C7.5146 0.686182 7.9012 0.686182 8.1395 0.941454Z"
              fill="#F2F2F2"
            />
          </svg>
          {current + 1}/{news.length}
          <svg
            width="30"
            height="18"
            viewBox="0 0 30 18"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            onClick={() => showSlide(next, goNext)}
          >
            <path
              d="M21.8605 0.941454C21.622 1.19651 21.622 1.61045 21.8605 1.8654L27.9158 8.3465H0.610499C0.273402 8.3465 0 8.63913 0 8.99993C0 9.36073 0.273402 9.65336 0.610499 9.65336H27.9157L21.8603 16.1346C21.622 16.3896 21.6219 16.8035 21.8604 17.0586C22.0988 17.3138 22.4854 17.3138 22.7238 17.0585L29.8212 9.4619C29.9404 9.33438 30 9.1671 30 8.99993C30 8.83277 29.9404 8.66549 29.8211 8.53796L22.7237 0.941454C22.4854 0.686182 22.0988 0.686182 21.8605 0.941454Z"
              fill="#F2F2F2"
            />
          </svg>
        </div>
      </div>
    </div>
  );
};
