import React, { useEffect, useState } from "react";
import { Divider } from "../../atoms";
import { Slider, SearchContainer } from "../../organisms";
import { LayoutTwoFooters } from "../../layout";
import { CardList } from "../../molecules";
import { useApiRequest, usePagination } from "../../../hooks";

import "./index.scss";


// const data = async () => {
//   const { services } = await getRequest(`services`);
// };



export const Home = () => {
  const [items, setItems] = useState([]);
  const { getRequest } = useApiRequest();

  const { data, count, handleChange } = usePagination([...items], 6);

  useEffect(() => {
    const getData = async () => {
      const { services } = await getRequest(`services`);
      setItems([...services]);
      handleChange(1);
    };
    getData();
  }, []);


  return (
    <LayoutTwoFooters>
      <div id="home">
        <div className="search-section-container">
          <SearchContainer />
        </div>
        <Divider label="Noticias" />
        <Slider />
        <Divider label="Destacados" />
        <CardList data={data} />
      </div>
    </LayoutTwoFooters>
  )
};
