import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import Pagination from "@material-ui/lab/Pagination";
import { usePagination, useApiRequest } from "../../../hooks";
import { Card, ResultFilters, CardList} from "../../molecules";
import "./index.scss";

// const getData = async () => {
//   const response = await fetch(`https://pokeapi.co/api/v2/pokemon?limit=20`, {
//     method: "GET",
//     headers: {
//       "Content-Type": "application/json",
//     },
//   });

import "./index.scss";

export const SearchResults = () => {
  const { query } = useParams();
  const [items, setItems] = useState([]);
  const {getRequest} = useApiRequest(); 

  const { data, count, handleChange } = usePagination([...items], 5);

  useEffect(() => {
    const getData = async () => {
      const {services} = await getRequest(`services/list?q=${query}`);
      setItems([...services]);
      handleChange(1);
    };
    getData();
  }, []);

  const changePage = (event, value) => handleChange(value);

  return (
    <Grid container alignContent="center" className="resultsContainer">
      <Grid
        item
        container
        justifyContent="center"
        alignContent="center"
        xs={12}
        sm={12}
      >
        <ResultFilters />
      </Grid>
      <CardList data={data} xs={4} sm={4} />
      <Grid item xs={12} align="center">
        <Pagination
          count={count}
          className="pagination"
          variant="outlined"
          shape="rounded"
          onChange={changePage}
        />
      </Grid>
    </Grid>
  );
};
