import React, { useCallback, useEffect } from "react";
import { useHistory } from "react-router-dom";
import Autocomplete from "@material-ui/lab/Autocomplete";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import SearchIcon from "@material-ui/icons/Search";

import { useFetch, FETCHSTATES, useSimpleForm } from "../../../hooks";

const formShape = {
  searchTerm: "",
};

export function SearchBar() {
  const history = useHistory();
  const {
    values: { searchTerm },
    onChange,
  } = useSimpleForm(formShape);
  const [data, status] = useFetch(`services?q=${searchTerm}`);

  const handleKeyDown = useCallback(
    (event) => {
      if (event.key === "Enter" && searchTerm.length) {
        history.push(`/results/${searchTerm}`);
      }
    },
    [history, searchTerm]
  );

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);

    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [handleKeyDown]);

  // const options = status === FETCHSTATES.loading ? [] : data.services;
  // const handleChange = (e, value, reason) => {
  //   if (reason === "select-option") {
  //     history.push(`/results/${value.name}`);
  //   }
  // };

  return (
    <Autocomplete
      id="custom-input-demo"
      disableClearable
      clearOnBlur={false}      
      filterOptions={(myOpts, state) => {
        return state.inputValue.length ? myOpts : [];
      }}
      forcePopupIcon={false}
      getOptionLabel={(option) => option.name}
      renderInput={(params) => (
        <Grid container item xs={10} className="search">
          <Grid item xs={10}>
            <TextField
              {...params}
              name="searchTerm"
              onChange={onChange}
              InputProps={{ ...params.InputProps, disableUnderline: true }}
              placeholder="Buscar"
              className="input-search"
            />
          </Grid>
          <Grid item xs={2} align="right" className="search-icon-container">
            <div className="search-icon-background">
              <SearchIcon className="search-icon" />
            </div>
          </Grid>
        </Grid>
      )}
    />
  );
}
