import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import Pagination from "@material-ui/lab/Pagination";

import { usePagination, useFetch, FETCHSTATES } from "../../../hooks";
import { CardList, ResultFilters } from "../../molecules";
import "./index.scss";

export const SearchResults = () => {
  const { query } = useParams();
  const [servicesData, status] = useFetch(`services/list?q=${query}`);

  const paginationInput =
    status === FETCHSTATES.loading ? [] : servicesData.services;
  const { data, count, handleChange } = usePagination([...paginationInput], 6);

  const actualResults = paginationInput.length;
  useEffect(() => {
    if (actualResults > 0) handleChange(1);
  }, [actualResults]);

  const changePage = (event, value) => handleChange(value);

  console.log("count: ", count);

  return (
    <Grid container alignContent="center" className="resultsContainer">
      <Grid
        item
        container
        justifyContent="center"
        alignContent="center"
        xs={12}
        sm={12}
      >
        <ResultFilters />
      </Grid>
      <CardList data={data} xs={4} sm={4} />
      <Grid item xs={12} align="center">
        <Pagination
          count={count}
          className="pagination"
          variant="outlined"
          shape="rounded"
          onChange={changePage}
        />
      </Grid>
    </Grid>
  );
};
