import React, {useEffect, useState} from 'react';
import {TabDescriptor} from '../../organisms';
import {useApiRequest, usePagination} from '../../../hooks';
import Grid from "@material-ui/core/Grid";
import Pagination from "@material-ui/lab/Pagination";
import {CardList} from '../../molecules';
import { BasicLayout } from "../../layout";

export const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [services, setServices] = useState([]);
  // Selected category
  const [category, setCategory] = useState(undefined);
  const {getRequest} = useApiRequest();
  const { data, count, page, handleChange } = usePagination([...services], 6);


  useEffect(() => {
    const getData = async () => {
      const { services } = await getRequest(`service-type/`);
      setCategories([...services]);
    };
    const getServicesByCategories = async () => {
      const {services} = await getRequest(`service-type/${category}/services`);
      if (services) setServices(services);
      handleChange(1);
    };
    const getAllData = async () => {
      await getData();
      await getServicesByCategories();
    };
    getAllData();
  }, [category]);

  const handleApiOfCategories = (index) => {
    setCategory(index);
  };

  const changePage = (event, value) => handleChange(value);

  return (
    <BasicLayout>
      <Grid container>
        <Grid item xs={12} sm={12} align="center"> 
          <TabDescriptor tabs={categories} click={handleApiOfCategories}/>
        </Grid>
        <CardList data={data}  xs={4} sm={4} />
        <Grid item xs={12} align="center">
          <Pagination
            count={count}
            className="pagination"
            variant="outlined"
            shape="rounded"
            page={page}
            onChange={changePage}
          />
        </Grid>
      </Grid>
    </BasicLayout>
  );
};
