import React from "react";
import { Button, ButtonGroup, TextField } from "@material-ui/core/";
import { makeStyles } from "@material-ui/core/styles";
import { Facebook, Apple, LinkedIn } from "@material-ui/icons";

import { useSimpleForm, useAuthentication } from "../../../../hooks";

import "./index.scss";

const useStyles = makeStyles((theme) => ({
  paper: {
    margin: theme.spacing(0, 4),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  groupbtn: {
    marginBottom: 40,
  },
  btnFace: {
    width: 300,
    borderRadius: 10,
    marginBottom: 20,
    backgroundColor: "#1877F2",
    color: "white",
  },
  btnGoogle: {
    width: 300,
    borderRadius: 10,
    marginBottom: 20,
    backgroundColor: "#FFFFFF",
    color: "black",
  },
  btnApple: {
    width: 300,
    borderRadius: 10,
    marginBottom: 20,
    backgroundColor: "#000000",
    color: "white",
  },
  btnLink: {
    width: 300,
    borderRadius: 10,
    marginBottom: 20,
    backgroundColor: "#FFFFFF",
    color: "#0A66C2",
  },
  groupButtons: {
    // backgroundColor: "#F5F5F5",
    color: "#8F8F8F",
  },
}));

export const RegisterOne = ({
  isProvider,
  setIsProvider,
  setUserValuesHard,
  goNext,
}) => {
  const classes = useStyles();
  const { handleAuthentication, operationTypes, providers } =
    useAuthentication();

  const { values, onChange } = useSimpleForm({
    email: "",
    password: "",
  });

  const handleUserCreation = (type, ...provider) =>
    handleAuthentication(type, ...provider)
      .then((user) => {
        setUserValuesHard(user);
        goNext();
      })
      .catch((err) => {
        console.log("error is: ", err);
      });

  return (
    <div className={classes.paper} id="register-step-one">
      <ButtonGroup disableElevation variant="contained">
        <Button
          className={classes.groupButtons}
          color={!isProvider ? "primary" : "default"}
          onClick={() => setIsProvider(false)}
        >
          Soy un usuario
        </Button>
        <Button
          className={classes.groupButtons}
          color={isProvider ? "primary" : "default"}
          onClick={() => setIsProvider(true)}
        >
          Soy un profesional
        </Button>
      </ButtonGroup>
      {/* <div className="email-input-container">
        <TextField id="outlined-basic" label="email" variant="outlined" fullWidth/>
      </div> */}
      {/* <div className={classes.groupbtn}></div> */}
      <div className="form-container">
        <TextField
          fullWidth={true}
          variant="outlined"
          margin="normal"
          required
          id="email"
          label="Usuario"
          name="email"
          autoComplete="email"
          className="field"
          value={values.email}
          onChange={onChange}
        />
        <TextField
          fullWidth={true}
          variant="outlined"
          margin="normal"
          required
          name="password"
          label="Contraseña"
          type="password"
          id="password"
          autoComplete="current-password"
          className="field"
          value={values.password}
          onChange={onChange}
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className="login-button"
          onClick={() =>
            handleUserCreation(
              operationTypes.signUpByPassAndEmail,
              values.email,
              values.password
            )
          }
        >
          Crear
        </Button>
      </div>
      <Button
        variant="contained"
        className={classes.btnFace}
        onClick={() =>
          handleUserCreation(operationTypes.oauth, providers.facebookProvider)
        }
      >
        <Facebook />
        Continuar con Facebook
      </Button>
      <Button
        variant="contained"
        className={classes.btnGoogle}
        onClick={() =>
          handleUserCreation(operationTypes.oauth, providers.googleProvider)
        }
      >
        Continuar con Google
      </Button>
      <Button variant="contained" className={classes.btnApple}>
        <Apple />
        Continuar con Apple
      </Button>
      <Button variant="contained" className={classes.btnLink}>
        <LinkedIn />
        Continuar con Linkedin
      </Button>
    </div>
  );
};

RegisterOne.defaultProps = {
  goBack: () => true,
};
