import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";

import { PrimaryButton } from "../../atoms";
import { Error } from "../../molecules";
import { BasicLayout } from "../../layout";
import { UserContext } from "../../../context";
import { useFetch, FETCHSTATES } from "../../../hooks";
import { View } from "./View";
import { Form } from "./Form";
import "./index.scss";

export const MyProfile = () => {
  const { isUserLogged, ...user } = useContext(UserContext);
  const [isEditing, setIsEditing] = useState(false);
  const [data, userTypesRequestState] = useFetch(`user/${user.id}/type`);

  const toggleEditForm = () => setIsEditing(!isEditing);

  if (!isUserLogged) {
    return (
      <BasicLayout>
        <div id="my-profile">
          <Error
            title="Ups! parece que no estás loggeado"
            message="¡Te invitamos a loggearte o crear un perfil si no lo tienes!"
          >
            <Link to="/login">
              <PrimaryButton>Login</PrimaryButton>
            </Link>

            <p>Ó</p>
            <Link to="/sign-up">
              <PrimaryButton>Sign up</PrimaryButton>
            </Link>
          </Error>
        </div>
      </BasicLayout>
    );
  }

  const { userTypes } = data;
  const isServiceProvider =
    FETCHSTATES.loading === userTypesRequestState
      ? false
      : userTypes.map((ut) => ut.name).includes("Provider");

  return (
    <BasicLayout>
      <div id="my-profile">
        {isEditing ? (
          <Form user={user} toggleEditForm={toggleEditForm} />
        ) : (
          <View
            user={user}
            toggleEditForm={toggleEditForm}
            isServiceProvider={isServiceProvider}
          />
        )}
      </div>
    </BasicLayout>
  );
};
