import React, { useRef } from "react";
import Button from "@material-ui/core/Button";
import SearchIcon from "@material-ui/icons/Search";
import InfoIcon from "@material-ui/icons/Info";

import "./index.scss";

export const RegisterFourth = ({
  user,
  onUserChange,
  service,
  onServiceChange,
}) => {
  const avatarInputRef = useRef();
  const avatarFullWidthInputRef = useRef();
  const bannerInputRef = useRef();

  const toggleFileSelector = (e, ref) => {
    e.preventDefault();
    ref.current.click();
  };

  return (
    <div className="user-form">
      <div className="file-input">
        <p className="label">Avatar para pantalla completa</p>
        <div>
          <input
            type="file"
            name="avatar"
            hidden
            onChange={onUserChange}
            ref={avatarInputRef}
          />
          <Button
            type="submit"
            variant="contained"
            color="primary"
            className="input-button"
            onClick={(e) => toggleFileSelector(e, avatarInputRef)}
          >
            <SearchIcon /> Archivo de imagen
          </Button>
          <p className="text-select">
            {user.avatar ? user.avatar.name : "Selecionar archivo"}
          </p>
        </div>
        <p className="info">
          <InfoIcon /> Tama単o recomendado 180 X 180 px
        </p>
      </div>
      <div className="file-input">
        <p className="label">Avatar para pantalla peque単a</p>
        <div>
          <input
            type="file"
            name="avatarFullWidth"
            hidden
            onChange={onUserChange}
            ref={avatarFullWidthInputRef}
          />
          <Button
            type="submit"
            variant="contained"
            color="primary"
            className="input-button"
            onClick={(e) => toggleFileSelector(e, avatarFullWidthInputRef)}
          >
            <SearchIcon /> Archivo de imagen
          </Button>
          <p className="text-select">
            {user.avatarFullWidth
              ? user.avatarFullWidth.name
              : "Selecionar archivo"}
          </p>
        </div>
        <p className="info">
          <InfoIcon /> Tama単o recomendado 120 X 120 px
        </p>
      </div>
      <div className="file-input">
        <p className="label">Banner</p>
        <div>
          <input
            type="file"
            name="banner"
            hidden
            onChange={onServiceChange}
            ref={bannerInputRef}
          />
          <Button
            type="submit"
            variant="contained"
            color="primary"
            className="input-button"
            onClick={(e) => toggleFileSelector(e, bannerInputRef)}
          >
            <SearchIcon /> Archivo de imagen
          </Button>
          <div>
            <p className="text-select">
              {service.banner ? service.banner.name : "Selecionar archivo"}
            </p>
          </div>
        </div>
        <p className="info">
          <InfoIcon /> Tama単o recomendado 180 X 250 px
        </p>
      </div>
    </div>
  );
};
