import React, {useEffect, useState} from 'react';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import { Desciptor } from "../../molecules";
import {ListIcons} from '../../../resources/icons';
import {useApiRequest} from '../../../hooks';
import {BasicLayout} from '../../layout';

import './index.scss';

const {ping_drop, start_half, text_snippet} = ListIcons;

export const ServiceDetailScreen = (props) => {
  const id = props?.match?.params?.id;
  const {getRequest} = useApiRequest();
  const [data, setData] = useState({});

  useEffect(() => {
    const getData = async () => {
      const { service } = await getRequest(`services/${id}`);
      setData({...service});
      console.log(service);
    };
    getData();
  }, []);

  return (
    <BasicLayout>
    <Grid container>
      <Grid item xs={12} sm={5}>
        <div className='service-detail__image-container'>
          <img src={data?.logoNormalSize} alt='banner service'/>
        </div>
      </Grid>
      <Grid
        container
        item
        xs={12} sm={7}
        className='service-detail__info-container'
      >
        <Grid item className='white-text justify-content service-detail__title' xs={8} sm={8}>
          <h1>{data?.name}</h1>
        </Grid>
        <Grid item className='white-text justify-content service-detail__professional-title ' xs={8} sm={8}>
          <h3>{data?.user?.name}</h3>
        </Grid>
        <Grid item className='white-text justify-content service-detail__description' xs={8} sm={8}>
          <p>{data?.longDescription}</p>
        </Grid>
        <Grid item container alignContent='center'  xs={8} sm={8}>
          <Grid item xs={6} sm={6} className='service-detail__price-container'>
            <span className='white-text service-detail__price'>$50.00</span>
          </Grid>
          <Grid item xs={6} sm={6}>
             <Button
              variant="contained"
              color="primary"
              disableElevation
              className='service-detail__button-booking'

             >
              Agendar cita
            </Button>            
          </Grid>
        </Grid>
      </Grid>
      <Grid
        item
        container
        justifyContent='center'
        xs={12}
        sm={12}>
        <Grid item xs={12} sm={12}>
          <h1>Detalles</h1>
        </Grid>
        <Grid item container xs={12} sm={9}>
          <Grid item xs={3}>
            <Desciptor
              name='Ubicacion'
              value='Madrid Spain'
              icon={ping_drop}
            />
          </Grid>
          <Grid item xs={3}>
            <Desciptor
              name='Rating'
              value='9/10'
              icon={start_half}
            />
          </Grid>
          <Grid item xs={3}>
            <Desciptor
              name='Experiencia'
              value='+5 Years'
              icon={text_snippet}
            />
          </Grid>
        </Grid>
      </Grid>
    </Grid>
    </BasicLayout>
    )
};
