import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { useParams } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import Pagination from "@material-ui/lab/Pagination";
import { usePagination, useApiRequest } from "../../../hooks";
import { ResultFilters, CardList } from "../../molecules";
import "./index.scss";

export const SearchResults = () => {
  const { query } = useParams();
  const [items, setItems] = useState([]);
  const { getRequest } = useApiRequest();
  const history = useHistory();

  const { data, count, handleChange } = usePagination([...items], 5);

  useEffect(() => {
    const getData = async () => {
      const { services } = await getRequest(`services/list?q=${query}`);
      setItems([...services]);
      handleChange(1);
    };
    getData();
  }, []);

  const changePage = (event, value) => handleChange(value);

  const onCardClick = ({ id }) => history.push(`/service-detail/${id}`);

  return (
    <Grid container alignContent="center" className="resultsContainer">
      <Grid
        item
        container
        justifyContent="center"
        alignContent="center"
        xs={12}
        sm={12}
      >
        <ResultFilters />
      </Grid>
      <CardList data={data} xs={4} sm={4} onCardClick={onCardClick} />
      <Grid item xs={12} align="center">
        <Pagination
          count={count}
          className="pagination"
          variant="outlined"
          shape="rounded"
          onChange={changePage}
        />
      </Grid>
    </Grid>
  );
};
