import React, { useReducer, createContext } from "react";

export const UserContext = createContext();

const LOGOUT = "LOGOUT";
const LOGGED = "LOGGED";
const LOGIN_FAILED = "LOGIN_FAILED";
const CHECK_USER_STATUS = "CHECK_USER_STATUS";

// const initalState = {
//   isUserLogged: false,
//   token: "",
//   name: "",
//   id: "",
//   email: "",
//   lastname: "",
//   loginErrors: [],
// };

const initalState = {
  token:
    "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..iBaxhKqHU2pXcaBhQiF6mb4FO3KU7j4WliFCz7DwN80",
  zipCode: "55221",
  city: "Santa Maria",
  phone: "705.495.2866 x07098",
  name: "Kameron",
  avatar: "https://cdn.fakercloud.com/avatars/jedbridges_128.jpg",
  id: "0d39df94-0545-46d2-8405-4c064f17ee2e",
  email: "Aron1@gmail.com",
  age: "44883",
  lastname: "Towne",
  isUserLogged: true,
};

const userContexReducer = (state, action) => {
  switch (action.type) {
    case LOGGED: {
      return {
        isUserLogged: true,
        ...action.payload.user,
      };
    }
    case LOGIN_FAILED: {
      return {
        ...state,
        isUserLogged: false,
        loginErrors: ["Usuario o contraseña incorrectos"],
      };
    }
    default: {
      return state;
    }
  }
};

export const UserProvider = (props) => {
  const [state, dispatch] = useReducer(userContexReducer, initalState);

  const actions = {
    logout: () => dispatch({ type: LOGOUT }),
    checkUserStatus: () => dispatch({ type: CHECK_USER_STATUS }),
    setUserData: (user) => dispatch({ type: LOGGED, payload: { user } }),
  };

  const value = {
    ...state,
    ...actions,
  };

  return (
    <UserContext.Provider value={value}>{props.children}</UserContext.Provider>
  );
};

export const UserConsumer = UserContext.Consumer;

export function withUserContext(Component) {
  return function WithUserContextComponent(props) {
    return (
      <UserContext.Consumer>
        {(user) => <Component {...props} user={user} />}
      </UserContext.Consumer>
    );
  };
}
