import React, {useEffect, useState} from 'react';
import {History} from '../../organisms';
import Calendar from 'tui-calendar'; /* ES6 */
import Grid from '@material-ui/core/Grid';
import { BasicLayout } from "../../layout";
import { Divider } from '../../atoms';
import 'tui-calendar/dist/tui-calendar.css';

// If you use the default popups, use this.
import 'tui-date-picker/dist/tui-date-picker.css';
import 'tui-time-picker/dist/tui-time-picker.css';

import './index.scss';

const monthNames = [
  'Enero',
  'Febrero',
  'Marzo',
  'Abril',
  'Mayo',
  'Junio',
  'Julio',
  'Agosto',
  'Septiembre',
  'Octubre',
  'Noviembre',
  'Diciembre'
];
const month = new Date();

export const HistoryScreen = () =>  {
  const [calendar, setCalendar] = useState(undefined); 

  useEffect(() => {
    !calendar && setCalendar(new Calendar('#calendar-id', {
      defaultView: 'month',
      taskView: true,
      useDetailPopup: true,
    }));

    calendar && function() {
      // This way be prevent a blue rectangle used by default for the library
      calendar.on({
        'beforeCreateSchedule': function(e) {
          //console.log('beforeCreateSchedule', e);
          e.guide.clearGuideElement();
        }
      })
      // This way we assign values on the created calendar
      calendar.createSchedules([
        {
          id: '1',
          calendarId: '0',
          title: 'my 2',
          category: 'time',
          dueDateClass: '',
          body: 'example of body',
          start: '2021-10-18T22:30:00+09:00',
          end: '2021-10-18T23:30:00+09:00',
          isReadOnly: true
        },
        {
          id: '2',
          calendarId: '0',
          title: 'second ',
          body: 'example of body',
          category: 'time',
          dueDateClass: '',
          start: '2021-10-18T17:30:00+09:00',
          end: '2021-10-18T17:31:00+09:00',
          isReadOnly: true    // schedule is read-only
        }
      ]);
    }();
  }, [calendar]);

  const nextMonth = () => {
    console.log(calendar);
    calendar.createSchedules([
      {
        id: '1',
        calendarId: '0',
        title: 'my 2',
        body: 'example of body',
        category: 'time',
        dueDateClass: '',
        start: '2021-10-18T22:30:00+09:00',
        end: '2021-10-19T02:30:00+09:00'
      },
      {
        id: '2',
        calendarId: '0',
        title: 'second ',
        category: 'time',
        body: 'example of body',
        dueDateClass: '',
        start: '2021-10-18T17:30:00+09:00',
        end: '2021-10-19T17:31:00+09:00',
        isReadOnly: true    // schedule is read-only
      }
    ])
  };

  return (
  <BasicLayout>
    <Grid container className='history-container'>
      <Grid item container xs={12} sm={12} justifyContent="center" align='left'>
        <Grid item xs={10} sm={10} align='center'>
          <h1 className='agenda-title'>Agenda</h1>
        </Grid>
        <Grid item xs={10} sm={10} className='container-history-calendar'>
          <h1>{monthNames[month.getMonth()]} {month.getFullYear()}</h1>
          <div className='calendar-container'>
            <div id='calendar-id'></div>
          </div>
        </Grid>
      </Grid>
      <Grid item xs={12} sm={12}>
        <Divider label='Resumen agenda'/>
        {
          [1,2,3].map((value) => <History key={value} data={value}/>)
        }
      </Grid>
    </Grid>
  </BasicLayout>
  );
};
