import React, { useEffect, useState } from "react";
import Grid from "@material-ui/core/Grid";
import Pagination from "@material-ui/lab/Pagination";
import { usePagination } from '../../../hooks';
import { Card, ResultFilters } from '../../molecules';
import './index.scss';

const getData = async () => {
  const response = await fetch(`https://pokeapi.co/api/v2/pokemon?limit=20`, {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
    },
  });

  const { results } = await response.json();
  return results;
};

export const SearchResults = () => {
  const [items, setItems] = useState([]);
  const { data, count, handleChange } = usePagination([...items], 6);

  const setData = async () => {
    let tempData = await getData();
    setItems([...tempData]);
    handleChange(1);
  };

  const changePage = (event, value) => {
    //console.log(value);
    handleChange(value);
  };

  useEffect(() => {
    setData();
  }, []);

  return (
    <Grid container alignContent='center' className='resultsContainer'>
      <Grid
        item
        container
        justifyContent='center'
        alignContent='center'
        xs={12} 
        sm={12}>
        <ResultFilters/>
      </Grid>
      <Grid
        item
        container
        alignContent='center'
        xs={12}
        sm={12}>
        {data.map((x) => (
          <Grid item xs={4} sm={4} align='center' key={x.name}>
            <Card serviceName={x.name}/>
          </Grid>
        ))}
      </Grid>
      <Grid item xs={12} align='center'>
        <Pagination
          count={count}
          className='pagination'
          variant='outlined'
          shape='rounded'
          onChange={changePage} />
      </Grid>
    </Grid>
  );
};
