import React, { useContext, useRef, useState } from "react";
import PropTypes from "prop-types";
import { FormControl, TextField } from "@material-ui/core";

import { Avatar, Divider, FieldTitle, PrimaryButton } from "../../../atoms";
import { AutoCompleteField } from "../../../organisms";
import { useSimpleForm, useApiRequest } from "../../../../hooks";
import { UserContext } from "../../../../context";
import { getFormDataFromObj } from "../../../../Utils";
import Icons from "../../../../resources/icons";
import "./index.scss";

const formShape = {
  name: "",
  lastname: "",
  phone: "",
  email: "",
  phonePrefix: "",
  city: "",
  zipCode: "",
  birthdate: "",
  password: "",
  passwordConfirm: "",
};

const getUserFields = (user) =>
  Object.keys(formShape).reduce((acc, key) => {
    acc[key] = user[key] || "";
    return acc;
  }, {});

export const Form = ({ user, toggleEditForm, isServiceProvider }) => {
  const { values, onChange } = useSimpleForm(getUserFields(user));
  const { patchRequest } = useApiRequest();
  const { setUserData } = useContext(UserContext);
  const avatarInputRef = useRef();
  const [temporaryAvatar, setTemproaryAvatar] = useState("");

  const toggleFileSelector = () => avatarInputRef.current.click();

  const showPreview = () => {
    const url = URL.createObjectURL(avatarInputRef.current.files[0]);
    setTemproaryAvatar(url);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (values.password && values.password !== values.passwordConfirm) {
      alert("las contaseñas no coinciden");
      return;
    }

    const { passwordConfirm, ...rest } = values;

    if (rest.password.length === 0) delete rest.password;
    const userFormData = getFormDataFromObj(rest);

    patchRequest(`user/${user.id}`, userFormData)
      .then(({ user: updatedUser }) => {
        setUserData(updatedUser);
        toggleEditForm();
      })
      .catch((err) => {
        console.log("there was an error", err);
      });
  };

  return (
    <div id="my-profile-edit-form">
      <form onSubmit={handleSubmit}>
        <Divider label="Editar información" />
        <div className="avatar-container">
          <Avatar
            width={238}
            height={238}
            className="user-avatar"
            alt="profile"
            isFromS3={!temporaryAvatar}
            src={temporaryAvatar || user.avatar}
          />
          <input
            type="file"
            hidden
            name="avatar"
            onChange={(e) => {
              showPreview(e);
              onChange(e);
            }}
            ref={avatarInputRef}
          />
          <div className="image-input-container" onClick={toggleFileSelector}>
            <object
              className="field-icon"
              data={Icons.placeholder}
              type="image/svg+xml"
            >
              <img alt="descriptor" src={Icons.placeholder} />
            </object>
            <label>Cambiar imagen de perfil</label>
          </div>
        </div>
        <div className="two-columns">
          <div className="column">
            <FormControl className="field-container">
              <FieldTitle icon={Icons.user} label="Nombre" />
              <TextField
                type="text"
                name="name"
                className="field-input"
                variant="outlined"
                margin="normal"
                value={values.name}
                onChange={onChange}
              />
            </FormControl>
            <FormControl className="field-container">
              <FieldTitle icon={Icons.email} label="Correo" />
              <TextField
                type="email"
                name="email"
                className="field-input"
                variant="outlined"
                margin="normal"
                value={values.email}
                onChange={onChange}
              />
            </FormControl>
            <FormControl className="field-container">
              <FieldTitle icon={Icons.user} label="Edad" />
              <TextField
                type="date"
                name="birthdate"
                className="field-input"
                variant="outlined"
                margin="normal"
                value={values.birthdate}
                onChange={onChange}
              />
            </FormControl>
          </div>
          <div className="column">
            <FormControl className="field-container">
              <FieldTitle icon={Icons.user} label="Apellidos" />
              <TextField
                type="text"
                name="lastname"
                className="field-input"
                variant="outlined"
                margin="normal"
                value={values.lastname}
                onChange={onChange}
              />
            </FormControl>
            <FormControl className="field-container">
              <FieldTitle icon={Icons.phone} label="Teléfono" />
              <div className="field-with-prefix">
                <AutoCompleteField
                  name="phonePrefix"
                  value={values.phonePrefix}
                  className="field-input prefix"
                  dataSourceResolver={(data) => data.countries}
                  onChange={onChange}
                  source="country"
                  labelResolver={({ dialCode }) => dialCode}
                  label="+1"
                />
                <TextField
                  type="text"
                  name="phone"
                  className="field-input"
                  variant="outlined"
                  margin="normal"
                  value={values.phone}
                  onChange={onChange}
                />
              </div>
            </FormControl>
            <FormControl className="field-container">
              <FieldTitle icon={Icons.location} label="Ubicación" />
              <div className="field-with-prefix">
                <AutoCompleteField
                  type="text"
                  name="zipCode"
                  className="field-input prefix"
                  value={values.zipCode}
                  onChange={onChange}
                  dataSourceResolver={(data) => data.countries}
                  source="country"
                  labelResolver={({ code }) => code}
                  label="ES"
                />
                <TextField
                  type="text"
                  name="city"
                  className="field-input"
                  variant="outlined"
                  margin="normal"
                  value={values.city}
                  onChange={onChange}
                />
              </div>
            </FormControl>
          </div>
        </div>
        <Divider label="Editar información de cuenta" />
        <div className="two-columns">
          <div className="column">
            <FormControl className="field-container">
              <FieldTitle icon={Icons.padlock} label="Contraseña" />
              <TextField
                autoComplete="on"
                type="password"
                onChange={onChange}
                name="password"
                className="field-input"
                variant="outlined"
                margin="normal"
              />
            </FormControl>
            <FormControl className="field-container">
              <FieldTitle icon={Icons.padlock} label="Confirmar Contraseña" />
              <TextField
                autoComplete="on"
                type="password"
                name="passwordConfirm"
                onChange={onChange}
                className="field-input"
                variant="outlined"
                margin="normal"
              />
            </FormControl>
          </div>
        </div>
        <div className="two-columns">
          <div className="column">
            <PrimaryButton onClick={handleSubmit}>Enviar</PrimaryButton>
          </div>
          <div className="column">
            <PrimaryButton onClick={toggleEditForm}>Cancelar</PrimaryButton>
          </div>
        </div>
      </form>
    </div>
  );
};

Form.propTypes = {
  user: PropTypes.object,
  toggleEditForm: PropTypes.func,
  isServiceProvider: PropTypes.bool,
};
