import React, { useContext } from "react";
import { Modal } from "@material-ui/core";

import { useSteper } from "../../../hooks";
import {
  BookingDatePickerModal,
  BookingDetailModal,
  BookingServicesModal,
  PaymentMethodsModal,
  BookingProcessEndModal,
} from "../../molecules";
import { useSimpleForm, useApiRequest } from "../../../hooks";
import { UserContext } from "../../../context";

const bookingFormShape = {
  date: "",
  kind: "",
  title: "",
  description: "",
  paymentMethod: "visa",
};

export const BookingProcess = ({ service, isBooking, close }) => {
  const { postRequest } = useApiRequest();
  const User = useContext(UserContext);
  const { values, onChange } = useSimpleForm(bookingFormShape);

  const steps = [
    BookingDatePickerModal,
    BookingDetailModal,
    BookingServicesModal,
    PaymentMethodsModal,
    BookingProcessEndModal,
  ];

  const { currentStep, goBack, goNext } = useSteper(steps);

  const bookService = () => {
    postRequest("booking", {
      ...values,
      user: User.id,
      service: service.id,
      title: service.name,
    })
      .then((res) => goNext())
      .catch((err) => console.log("error is: ", err));
  };

  const CurrentStepComponent = steps[currentStep];
  return (
    <Modal
      className="modal"
      open={isBooking}
      disableEnforceFocus={true}
      disableAutoFocus={true}
    >
      <CurrentStepComponent
        service={service}
        goBack={goBack}
        goNext={goNext}
        close={close}
        values={values}
        onChange={onChange}
        bookService={bookService}
      />
    </Modal>
  );
};
